from selenium import webdriver

from selenium.webdriver.support.ui import Select

import time
import re
from pyquery import PyQuery as pq

import codecs

from bs4 import BeautifulSoup

# 写 csv
def write_csv(sn, data):
	file = str(sn) + ".csv"

	with codecs.open(file,'a',encoding='utf-8') as f:
		f.write(data)
		f.close()


browser = webdriver.Chrome()  # 打开浏览器
sn = 1
for list in lists:
	sn += 1
	strs_list = ''
	for i in range(1, 11):
		url = list +"_"+ str(i) +".html"
		print(url)
		browser.get(url)  # 进入相关网站
		data = browser.find_elements_by_class_name("col-xs-12")[2]
		arr = data.text
		strs = ','.join(arr.split(' ')) + '\n'
		strs_list += strs

	write_csv(sn, strs_list)
	time.sleep(2)

browser.quit()
